      if ((charCode > 31 && (charCode < 48 || charCode > 57)) && charCode !== 46) {
        event.preventDefault();
      } else {

        let inputValue = parseInt(`${event.target.value}` + `${String.fromCharCode(charCode)}`)

        if (inputValue < 0 || inputValue > 99) {
          event.preventDefault();
        }

        return true;
      }
    }
  },
}
</script>
